import org.junit.jupiter.api.Test
import kotlin.test.assertEquals

class Day17Test {

    @Test
    fun test1() {
        val jetPattern = ">>><<><>><<<>><>>><<<>>><<<><<<>><>><<>>"
        val pyroclasticFlow = PyroclasticFlow(jetPattern)
        val height = pyroclasticFlow.heightOfRocks()
        assertEquals(3068, height)
    }

